mapper.select:
SELECT COUNT(*)
	FROM MEMBER
WHERE MEM_EMAIL = #{memberEmail}

mapper.update:
UPDATE MEMBER 
SET MEM_NAME = #{memberName},
		MEM_CONCERN = #{memberConcern},
		POINT = #{point}
WHERE MEM_NO = #{memberNo}

UPDATE MEMBER 
SET MEM_PWD = #{memberPwd}
WHERE MEM_EMAIL = #{memberEmail}

UPDATE MEMBER SET 
		INTRO = #{intro},
		MEM_NAME = #{memberName},
		LOCATION = #{location},
		SKILL_NO = #{skillNo}
WHERE MEM_NO = #{memberNo}

UPDATE MEMBER
SET MEM_PWD = #{memberPwd}
WHERE MEM_NO = #{memberNo}

UPDATE MEMBER
SET STATUS = 'N',
	MEM_EMAIL = null
WHERE MEM_NO = #{memberNo}
AND MEM_EMAIL = #{memberEmail}

UPDATE MEMBER
SET FILE_PATH = #{filePath}
WHERE MEM_NO = #{memberNo}
AND STATUS = 'Y'

UPDATE SCHEDULE
SET STATUS = 'N'
WHERE SCHE_NO = #{scheNo}


UPDATE MEMBER
SET STATUS = 'N'
WHERE MEM_NO = #{memberNo}

UPDATE MEMBER 
SET ACCOUNT = ACCOUNT + #{price}
WHERE MEM_NO = #{memberNo}

UPDATE MEMBER
SET PHONE = #{phone}
WHERE MEM_NO = #{memberNo}
